import React from "react";
import { useState ,useEffect} from "react";

const Weather = ({ formattedDate, formattedFutureDateTime }) => {
  const [weatherData, setWeatherData] = useState(null);
  const apiKey = '16204f83111c4fed9e691403231408';
  const city = 'Agra'; 

  useEffect(() => {
    fetch(`http://api.weatherapi.com/v1/current.json?key=${apiKey}&q=${city}&aqi=no`)
      .then(response => response.json())
      .then(data => setWeatherData(data))
      .catch(error => console.error('Error fetching weather data:', error));
  }, []);
  return (
    <div>
      <div>
        <p>{formattedDate}</p>
        <p> {formattedFutureDateTime}</p>
      </div>
      <div>
      <h1>Weather in {weatherData?.location?.name}</h1>
      {weatherData && (
        <div>
          <p>Temperature: {weatherData.current.temp_c}Â°C</p>
          <p>Condition: {weatherData.current.condition.icon}</p>
          {/* You can add more weather information here */}
        </div>
      )}
    </div>
    </div>
  );
};

export default Weather;
