import React, { useRef, useState } from "react";

import Validation from "./Validation";

import { useNavigate } from "react-router-dom";

const Registration = () => {
  const initialDetails = {
    name: "",
    UserName: "",
    email: "",
    number: "",
    checkbox: false,
  };

  const name = useRef();
  const email = useRef();
  const UserName = useRef();
  const number = useRef();

  const [details, setDetails] = useState(initialDetails);
  const [disable, setDisable] = useState("typing");

  const [errors, setError] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setDetails((prev) => {
      return { ...prev, [name]: value };
    });
  };
  let navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    setDetails(initialDetails);
    setDisable("submitted");
    setError(Validation(details));
    if (
      name.current.value &&
      UserName.current.value &&
      email.current.value &&
      number.current.value
    ) {
      localStorage.setItem("name", name.current.value);
      localStorage.setItem("UserName", UserName.current.value);
      localStorage.setItem("email", email.current.value);
      localStorage.setItem("number", number.current.value);
      alert("account Created Successfully!!");

      let path = `/Onboarding`;
      navigate(path);
    }
    console.log(details);
  };

  return (
    <div className="flex">
      <div className="relative w-full">
        <img className="w-full h-screen" src="" />
        <p className=" w-5/6 h-143 absolute bottom-16 text-white left-16  text-6xl font-semibold">
          Discover new things on Superapp
        </p>
      </div>

      <div className="relative w-full bg-black">
        <h4 className="w-72 h-24 absolute top-16 right-40 text-green-600 text-6xl font-single">
          Superapp
        </h4>
        <h4 className="w-72 h-28 absolute top-36 right-36 text-white">
          Create your new account{" "}
        </h4>
        <form onSubmit={handleSubmit}>
          <div className="absolute top-48">
            <input
              className="w-96 h-16 mb-4 bg-slate-800 rounded absolute top-0 left-60"
              type="text"
              placeholder="name"
              onChange={handleChange}
              value={details.name}
              ref={name}
            />
            <input type="text" placeholder="name" onChange={handleChange} value={details.name} ref={name} />

            {errors.name && <p className="">{errors.name}</p>}
            <input
              className="w-96 h-16 mb-4 bg-slate-800 rounded absolute top-20 left-60"
              type="text"
              placeholder="UserName"
              onChange={handleChange}
              value={details.UserName}
              ref={UserName}
            />

            {errors.Username && <p className="">{errors.Username}</p>}

            <input
              className="w-96 h-16 mb-4 bg-slate-800 rounded absolute top-40 left-60"
              type="email"
              placeholder="email"
              onChange={handleChange}
              value={details.email}
              ref={email}
            />

            {errors.email && <p className="">{errors.email}</p>}

            <input
              className="w-96 h-16 mb-4 bg-slate-800 rounded absolute top-60 left-60"
              type="number"
              placeholder="mobileNumber"
              onChange={handleChange}
              value={details.number}
              ref={number}
            />

            {errors.number && <p className="">{errors.number}</p>}

            <input
              className="absolute top-80 left-60"
              type="checkbox"
              name="checkbox"
              onChange={handleChange}
              value={details.checkbox}
            />
            <p className=" w-96 h-0 absolute top-80 left-64 text-white">
              Share my Registration Data with SuperApp
            </p>
          </div>
          <div className="relative top-80 ">
            <button
              className="w-96 h-12 absolute top-60 left-60 bg-green-600 rounded-3xl"
              onClick={handleSubmit}
              disabled={
                details.name.length === 0 ||
                details.UserName.length === 0 ||
                details.email.length === 0 ||
                details.number.length === 0 ||
                details.checkbox === true ||
                disable === "submitted"
              }
            >
              SIGN UP
            </button>

            <p className="text-white absolute top-72 left-60 w-96 h-4">
              By clicking on Sign up. you agree to Superapp
              <span className=" text-green-500">
                Terms and Conditions of Use
              </span>
            </p>
            <p className="text-white mb-4 absolute inset-y-80 left-56 w-96 h-4 p-4 ">
              To learn more about how Superapp collects, uses, shares and
              protects your personal data please head Superapp
              <span className=" text-green-500"> Privacy Policy</span>
            </p>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Registration;
