import React from "react";
import { useState ,useEffect} from "react";
import Clear from "./image/"

const Weather = ({ formattedDate, formattedFutureDateTime }) => {
  const [weatherData, setWeatherData] = useState(null);
  const apiKey = '16204f83111c4fed9e691403231408';
  const city = 'Agra'; 

  useEffect(() => {
    fetch(`http://api.weatherapi.com/v1/current.json?key=${apiKey}&q=${city}&aqi=no`)
      .then(response => response.json())
      .then(data => setWeatherData(data))
      .catch(error => console.error('Error fetching weather data:', error));
  }, []);


  const weatherIcons = {
    'Clear': 'Clear.png',
    'Partly cloudy': 'PartlyCloud.png',
    'Cloudy': 'Cloudy.png',
    'Light rain': 'LightRain.png',
    // Add more mappings as needed
  };

  const getWeatherIcon = condition => {
    const iconFileName = weatherIcons[condition];
    if (iconFileName) {
      return `/src/Components/image/${iconFileName}`;
    }
    return null;
  };
  return (
    <div>
      <div>
        <p>{formattedDate}</p>
        <p> {formattedFutureDateTime}</p>
      </div>
      <div>
      <h1>Weather in {weatherData?.location?.name}</h1>
      {weatherData && (
        <div>
        <div>
          
          <p>{weatherData.current.temp_c}Â°C</p>
          <p>{weatherData.current.pressure_mb}</p>
          <img src="" alt="" />
          </div>
          <div>

          <p> {weatherData.current.condition.text}</p>
          <img src={getWeatherIcon(weatherData.current.condition.text)} alt="" />
          </div>

        <p>{weatherData.current.humidity}</p>
        <img src="" alt="" />
        <p> {weatherData.current.wind_kph}</p>
        <img src="" alt="" />
        </div>
      )}
    </div>
    </div>
  );
};

export default Weather;
