import React, { useState } from "react";
import singer from "./singer.png";
import "./Register.css";

const Registration = () => {
  const initialDetails = {
    name: "",
    UserName: "",
    email: "",
    number: "",
    checkbox: false,
  };

  const [details, setDetails] = useState(initialDetails);;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setDetails((prev) => {
      return { ...prev, [name]: value };
    });
  };


  const isEnabled = details.name.length > 0 && details.UserName.length > 0 && details.email.length>0 && details.number.length;

  // const areAllFieldsFilled =
  //   details.name !== "" &&
  //   details.UserName !== "" &&
  //   details.email !== "" &&
  //   details.number !== "" &&
  //   details.checkbox === true;

  const handleSubmit = (e) => {
    e.preventDefault();
    setDetails(initialDetails);

    console.log(details);
  };

  return (
    <div>
      <div className="board">
        <img className="image" src={singer} alt="not found" />
        <p className="discover">Discover new things on Superapp</p>
      </div>
      <div className="form-Select">
        <h4 className="Heading">Superapp</h4>
        <h4 className="Heading2">Create your new account </h4>
        <form onSubmit={handleSubmit}>
          <input
            className="name"
            type="text"
            placeholder="name"
            name="name"
            onChange={handleChange}
            value={details.name}
          />
          <p className="error-message" onClick={handleSubmit}>
            {" "}
            Field is Required
          </p>
          <input
            className="UserName"
            type="text"
            placeholder="UserName"
            name="UserName"
            onChange={handleChange}
            value={details.UserName}
          />
          <p className="error-message" onClick={handleSubmit}>
            {" "}
            Field is Required
          </p>

          <input
            className="email"
            type="email"
            name="email"
            id="emailInputID"
            placeholder="email"
            onChange={handleChange}
            value={details.email}
          />
          <p className="error-message" onClick={handleSubmit}>
            {" "}
            Field is Required
          </p>

          <input
            className="number"
            type="number"
            name="number"
            id="numberInputID"
            placeholder="mobileNumber"
            onChange={handleChange}
            value={details.number}
          />
          <p className="error-message" onClick={handleSubmit}>
            {" "}
            Field is Required
          </p>

          <input
            className="checkbox"
            type="checkbox"
            name="checkbox"
            id=""
            onChange={handleChange}
            value={details.checkbox}
          />

          <p className="tickbox">Share my registration data with Superapp</p>

          <button
            className="signUp"
            onClick={handleSubmit}
            disabled={!isEnabled}
          >
            SIGN UP
          </button>

          <p className="line1">
            By clicking on Sign up. you agree to Superapp
            <span className="Terms">Terms and Conditions of Use</span>
          </p>
          <p className="line2">
            To learn more about how Superapp collects, uses, shares and protects
            your personal data please head Superapp
            <span className="Terms"> Privacy Policy</span>
          </p>
        </form>
      </div>
    </div>
  );
};

export default Registration;
