import React, { useState } from "react";
import singer from "./singer.png";
import "./Register.css";

const Registration = () => {
  const [Name, setName] = useState("");
  const [UserName, setUserName] = useState("");
  const [Email, setEmail] = useState("");
  const [Number, setNumber] = useState("");
  const [Tick, setTick] = useState(false);
  const [Message,setMessage]=useState("");
  const [isFilledForm,setisFilledForm]=useState(false);



  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleUserName = (event) => {
    setUserName(event.target.value);
  };
  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };
  const handleNumberChange = (event) => {
    setNumber(event.target.value);
  };
  const handleTick = (event) => {
    setTick(true);
  };

  const handleSubmitChange = (event) => {
    if (Name.length!==0 || UserName.length!==0 || Email.length!==0 || Number.length!==0) {
      event.preventDefault();
      setisFilledForm(true);
      
    } else {
      setMessage("Field is Required ")
    }
  };

  return (
    <div>
      <div className="board">
        <img className="image" src={singer} alt="not found" />
        <p className="discover">Discover new things on Superapp</p>
      </div>
      <div className="form-Select">
        <h4 className="Heading">Superapp</h4>
        <h4 className="Heading2">Create your new account </h4>
        <form action="submit">
          <input
            className="name"
            type="text"
            placeholder="name"
            onChange={handleNameChange}
            value={Name}
          />
          <p className="error-message">{Message}</p>
          <input
            className="UserName"
            type="text"
            placeholder="UserName"
            onChange={handleUserName}
            value={UserName}
          />
          
          <p className="error-message">{Message}</p>
          <input
            className="email"
            type="email"
            name="email"
            id="emailInputID"
            placeholder="email"
            onChange={handleEmailChange}
            value={Email}
          />
          
          <p className="error-message">{Message}</p>
          <input
            className="number"
            type="number"
            name="number"
            id="numberInputID"
            placeholder="mobileNumber"
            onChange={handleNumberChange}
            value={Number}
          />
          
          <p className="error-message">{Message}</p>
          <input
            className="checkbox"
            type="checkbox"
            name=""
            id=""
            onChange={handleTick}
            value={Tick}
          />
          
          <p className="error-message">{Message}</p>
          <p className="tickbox">Share my registration data with Superapp</p>

          <button className="signUp" onClick={handleSubmitChange} value={isFilledForm}>
            SIGN UP
          </button>

          <p className="line1">
            By clicking on Sign up. you agree to Superapp
            <span className="Terms">Terms and Conditions of Use</span>
          </p>
          <p className="line2">
            To learn more about how Superapp collects, uses, shares and protects
            your personal data please head Superapp
            <span className="Terms"> Privacy Policy</span>
          </p>
        </form>
      </div>
    </div>
  );
};

export default Registration;
