import React, { useEffect, useState } from "react";

const ActionMovies = ({ onDataReceived }) => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const fetchMovies = async () => {
      const url = "https://unogsng.p.rapidapi.com/genres";
      const options = {
        method: "GET",
        headers: {
          "X-RapidAPI-Key":
            "c9ddd88f2fmshcaa866a412a2684p1020cfjsnf3ec2a18103b",
          "X-RapidAPI-Host": "unogsng.p.rapidapi.com",
        },
      };

      try {
        const response = await fetch(url, options);
        const data = await response.json();
        setMovies(data.result);
      } catch (error) {
        console.error(error);
      }
    };

    fetchMovies();
  }, []);

  useEffect(() => {
    onDataReceived(movies);
  }, [movies, onDataReceived]);

  return (
    <div>
      <h1>Action Movies</h1>
      <ul>
        {movies && movies.length > 0 ? (
          movies.map((movie, index) => (
            <li key={index}>
              <img src={movie.image} alt={movie.title} />
              <p>{movie.title}</p>
            </li>
          ))
        ) : (
          <p>No action movies available</p>
        )}
      </ul>
    </div>
  );
};

export default ActionMovies;
