import React, { useEffect, useState } from "react";

const ActionMovies = ({ onDataReceived }) => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const fetchMovies = async () => {
    const fetch = require('node-fetch');

const url = 'https://api.themoviedb.org/3/account/20306918/watchlist/movies?language=en-US&page=1&sort_by=created_at.asc';
const options = {
  method: 'GET',
  headers: {
    accept: 'application/json',
    Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI0ODIzN2IxMGNmMjI5ZGI5M2M3M2ZkMzUyNmQ2N2QwNSIsInN1YiI6IjY0ZGM2MjZiYTNiNWU2MDBhY2MxY2MxOSIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.QDMhx_QpGlHGIRYIoTtB56z3X17riLe6B0hPOFgSE4s'
  }
};

fetch(url, options)
  .then(res => res.json())
  .then(json => console.log(json))
  .catch(err => console.error('error:' + err));
    };

    fetchMovies();
  }, []);

  useEffect(() => {
    onDataReceived(movies);
  }, [movies, onDataReceived]);

  return (
    <div>
      <h1>Action Movies</h1>
      <ul>
        {movies && movies.length > 0 ? (
          movies.map((movie, index) => (
            <li key={index}>
              <img src={movie.image} alt={movie.title} />
              <p>{movie.title}</p>
            </li>
          ))
        ) : (
          <p>No action movies available</p>
        )}
      </ul>
    </div>
  );
};

export default ActionMovies;
