import React, { useState, useEffect } from 'react';

const API_KEY = '';
const API_URL = `http://www.omdbapi.com/?apikey=${API_KEY}&t=`;

function MovieSearch() {
  const [movieData, setMovieData] = useState({});
  const movieTitle = 'The Avengers'; // Replace this with the movie title you want to search for

  useEffect(() => {
    async function fetchMovieData() {
      try {
        const response = await fetch(`${API_URL}${encodeURIComponent(movieTitle)}`);
        const data = await response.json();
        setMovieData(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }

    fetchMovieData();
  }, []);

  return (
    <div>
      <h1>OMDB Movie Information</h1>

      {movieData.Title && (
        <div>
          <h2>{movieData.Title}</h2>
          <p>{movieData.Plot}</p>
          <img src={movieData.Poster} alt={movieData.Title} />
        </div>
      )}
    </div>
  );
}

export default MovieSearch;
