import React, { useState } from "react";
import rockstar from "./image/rockstar.png";
import "./Register.css";
import Entry from "./Entry";

const Register = () => {
  const [formData, setFormData] = useState({
    name: " ",
    Username: " ",
    email: "",
    number: "",
  });

  const [errors,setErrors]=useState({});


  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    const newErrors = {};

    if (formData.name.trim() === '') {
      newErrors.name = 'Name is required';
    }

    if (formData.username.trim() === '') {
      newErrors.username = 'Username is required';
    }

    if (!formData.email.includes('@')) {
      newErrors.email = 'Invalid email address';
    }

    if (isNaN(formData.number) || formData.number.trim() === '') {
      newErrors.number = 'Please enter a valid number';
    }

    setErrors(newErrors);

    // If there are no errors, you can proceed with submitting the form data
    if (Object.keys(newErrors).length === 0) {
      // Submit form data to the server or perform further actions
      console.log('Form submitted:', formData);
    }

  };

  return (
    <div className="register_page">
      <div className="left_container">
        <img src={rockstar} alt="" />
        <h1 className="discover">Discover New things on SuperApp</h1>
      </div>
      <div className="right_container">
        <h1 className="heading">Super App</h1>
        <p className="subheading">Create your New Account </p>
        <form onSubmit={handleSubmit}>
          <div className="entry_tags">
            <Entry
              type="text"
              placeholder="Name"
              onChange={handleChange}
              value={formData.name}
              err
            />

            <Entry
              type="text"
              placeholder="UserName"
              onChange={handleChange}
              value={formData.Username}
            />
            <Entry
              type="email"
              placeholder="E-mail Address"
              onChange={handleChange}
              value={formData.email}
            />
            <Entry
              type="number"
              placeholder="Number"
              onChange={handleChange}
              value={formData.number}
            />
          </div>
        </form>

        <div className="checkbox_part">
          <input className="checkbox" type="checkbox" name="" id="" />
          <p className="text"> Share my Registration data with Superapp</p>
        </div>
        <button className="button">SIGN UP</button>
        <p className="heading2">
          By clicking on Sign up. you agree to Superapp{" "}
          <span className="span1">Terms and Conditions of Use</span>
          <p className="last_heading">
            To learn more about how Superapp collects, uses, shares and protects
            your personal data please head Superapp
            <span className="span2">Privacy Policy</span>
          </p>
        </p>

        <p className="last_heading">
          To learn more about how Superapp collects, uses, shares and protects
          your personal data please head Superapp
          <span className="">Privacy Policy</span>
        </p>
      </div>
    </div>
  );
};

export default Register;
