import React, { useState } from "react";
import singer from "./singer.png";
import "./Register.css";

const Registration = () => {
  const [Name, setName] = useState("");
  const [UserName, setUserName] = useState("");
  const [Email,setEmail]=useState("");


  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleUserName = (event) => {
    setUserName(event.target.value);
  };
  const handleEmailChange=(event)


  return (
    <div>
      <div className="board">
        <img className="image" src={singer} alt="not found" />
        <p className="discover">Discover new things on Superapp</p>
      </div>
      <div className="form-Select">
        <h4 className="Heading">Superapp</h4>
        <h4 className="Heading2">Create your new account </h4>
        <form action="submit">
          <input
            className="name"
            type="text"
            placeholder="name"
            onChange={handleNameChange}
            value={Name}
          />
          <input
            className="UserName"
            type="text"
            placeholder="UserName"
            onChange={handleUserName}
            value={UserName}
          />
          <input
            className="email"
            type="email"
            name=""
            id=""
            placeholder="email"
            onChange={handleEmailChange}
          />
          <input
            className="number"
            type="number"
            name=""
            id=""
            placeholder="mobileNumber"
          />
          <input className="checkbox" type="checkbox" name="" id="" />
          <p className="tickbox">Share my registration data with Superapp</p>

          <button className="signUp">SIGN UP</button>

          <p className="line1">
            By clicking on Sign up. you agree to Superapp
            <span className="Terms">Terms and Conditions of Use</span>
          </p>
          <p className="line2">
            To learn more about how Superapp collects, uses, shares and protects
            your personal data please head Superapp
            <span className="Terms"> Privacy Policy</span>
          </p>
        </form>
      </div>
    </div>
  );
};

export default Registration;
