import React, { useEffect, useState } from "react";

const ActionMovies = ({onDataReceived}) => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const fetchMovies = async () => {
      const url =
        "https://imdb188.p.rapidapi.com/api/v1/searchIMDB?query=action";
      const options = {
        method: "GET",
        headers: {
          "X-RapidAPI-Key":
            "40a2f2dd5cmshf909a04db28de45p171433jsn3f3750550338",
          "X-RapidAPI-Host": "imdb188.p.rapidapi.com",
        },
      };

      try {
        const response = await fetch(url, options);
        const data = await response.json();
        setMovies(data.result);
      } catch (error) {
        console.error(error);
      }
    };
    fetchMovies();
  }, []);

  useEffect(()=>{
    onDataReceived(movies)
  },[movies,onDataReceived]);

  return (
    <div>
      <h1>Action Movies</h1>
      <ul>
        {}
        {movies.map((movie, index) => (
          <li key={index}>
            <img src={movie.image} alt={movie.title} />
            <p>{movie.title}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ActionMovies;
