import React from "react";
import toggle from "./image/toggle.png";
import ActionMovies from "./ActionMovies";
import "./Entertain.css";


const Entertain = () => {
    const handleMovieData=(movies)=>{
        console.log('Received movies:', movies);   
    }
  return (
    <div className="container2">
      <div className="header">
        <h3>SuperApp</h3>
        <img  className="toggle" src={toggle} alt="" />
      </div>

      <h1>Entertainment According to your Choice</h1>
      <h4  className="sub_">Action Movies</h4>
      <div className="actionMovies">
        <ActionMovies onDataReceived={handleMovieData} />
      </div>
      <h4 className="sub_">Thriller Movies</h4>
      <div className="actionMovies">
        <ActionMovies onDataReceived={handleMovieData} />
      </div>
      <h4 className="sub_">fantasy Movies</h4>
      <div className="actionMovies">
        <ActionMovies onDataReceived={handleMovieData} />
      </div>
     
    </div>
  );
};

export default Entertain;
