    const [weatherData, setWeatherData] = useState(null);
  const API_KEY = "c5fcc0f3eeec458984c62114231408";
   // Replace with the desired city


  useEffect(() => {
    fetch(`http://api.weatherapi.com/v1/current.json?key=c5fcc0f3eeec458984c62114231408&q=Agra&aqi=yes`)
      .then((response) => response.json())
      .then((data) => setWeatherData(data));
  }, []);


  if (!weatherData) {
    return <div>Loading...</div>;
  }

