import React, { useState } from "react";
import rockstar from "./image/rockstar.png";
import "./Register.css";

const Register = () => {
  const initialValues = { name: " ", username: " ", email: " ", number: "  " };
  const [formValues, setformValues] = useState(initialValues);

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setformValues((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const newErrors = {};

    if (formValues.name.trim() === "") {
      newErrors.name = "Name is required";
    }

    if (formValues.username.trim() === "") {
      newErrors.username = "Username is required";
    }

    if (!formValues.email.includes("@")) {
      newErrors.email = "Invalid email address";
    }

    if (isNaN(formValues.number) || formValues.number.trim() === "") {
      newErrors.number = "Please enter a valid number";
    }

    setErrors(newErrors);

    // If there are no errors, you can proceed with submitting the form data
    if (Object.keys(newErrors).length === 0) {
      // Submit form data to the server or perform further actions
      console.log("Form submitted:", formValues);
    }
  };

  return (
    <div className="register_page">
      <div className="left_container">
        <img src={rockstar} alt="" />
        <h1 className="discover">Discover New things on SuperApp</h1>
      </div>
      <div className="right_container">
        <h1 className="heading">Super App</h1>
        <p className="subheading">Create your New Account </p>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            placeholder="Name"
            value={formValues.name}
            onChange={handleChange}
          />
          <input
            type="text"
            name="username"
            placeholder="username"
            value={formValues.username}
            onChange={handleChange}
          />
          <input
            type="text"
            name="email"
            placeholder="Email"
            value={formValues.email}
            onChange={handleChange}
          />
          <input
            type="number"
            name="number"
            placeholder="Number"
            value={formValues.number}
            onChange={handleChange}
          />

          <button className="button" type="submit">
            SIGN UP
          </button>
        </form>

        <p className="heading2">
          By clicking on Sign up. you agree to Superapp{" "}
          <span className="span1">Terms and Conditions of Use</span>
          <br />
          To learn more about how Superapp collects, uses, shares and protects
          your personal data please head Superapp
          <span className="span2">Privacy Policy</span>
        </p>

        <p className="last_heading">
          To learn more about how Superapp collects, uses, shares and protects
          your personal data please head Superapp
          <span className="">Privacy Policy</span>
        </p>
      </div>
    </div>
  );
};

export default Register;
