import React from "react";
import { format, addDays } from "date-fns";

const DateTimeComponent = () => {
  // Get the current date
  const currentDate = new Date();

  // Format the current date using date-fns
  const formattedDate = format(currentDate, "MMMM d, yyyy");

  // Add 3 days to the current date
  const futureDate = addDays(currentDate, 3);

  // Format the future date and time
  const formattedFutureDateTime = format(futureDate, "MMMM d, yyyy HH:mm:ss");
};
const Weather = (props) => {
  return (
    <div>
      <div>
      <p>Current Date: {formattedDate}</p>
      <p>Future Date and Time: {formattedFutureDateTime}</p>
      </div>
      <div></div>
    </div>
  );
};

export default Weather;
