import React from "react";
import { useState ,useEffect} from "react";
import Clear from "./image/Clear.png";
import Cloudy from "./image/Cloudy.png";
import LightRain from "./image/LightRain.png";
import PartlyCloud from "./image/PartlyCloud.png";
import speed from "./image/speed.png";
import Pressure from "./image/Pressure.png";
import Humidity from "./image/Humidity.png";

import "./Weather.css";




const Weather = ({ formattedDate, formattedFutureDateTime }) => {
  const [weatherData, setWeatherData] = useState(null);
  const apiKey = '16204f83111c4fed9e691403231408';
  const city = 'Agra'; 

  useEffect(() => {
    fetch(`http://api.weatherapi.com/v1/current.json?key=${apiKey}&q=${city}&aqi=no`)
      .then(response => response.json())
      .then(data => setWeatherData(data))
      .catch(error => console.error('Error fetching weather data:', error));
  }, []);


  const weatherIcons = {
    'Clear': 'Clear.png',
    'Partly cloudy': 'PartlyCloud.png',
    'Cloudy': 'Cloudy.png',
    'Light rain': 'LightRain.png',
    // Add more mappings as needed
  };

  const getWeatherIcon = condition => {
    const iconFileName = weatherIcons[condition];
    if (iconFileName) {
      return `/src/Components/image/${iconFileName}`;
    }
    return null;
  };
  return (
    <div>
      <div className="Date">
        <p className="date_format">{formattedDate}</p>
        <p  className="ti"> {formattedFutureDateTime}</p>
      </div>
      <div>
      {weatherData && (
        <div className="back">
        <div>
          
          <p>{weatherData.current.temp_c}Â°C</p>
          <p>{weatherData.current.pressure_mb}</p>
          <img src={Pressure}alt="not found" />
          </div>
          <div>

          <p> {weatherData.current.condition.text}</p>
          <img src={getWeatherIcon(weatherData.current.condition.text)} alt="" />
          </div>

        <p>{weatherData.current.humidity}</p>
        <img src={Humidity} alt="" />
        <p> {weatherData.current.wind_kph}</p>
        <img src={speed} alt="" />
        </div>
      )}
    </div>
    </div>
  );
};

export default Weather;
