    fetchMovies();
  }, []);

  useEffect(() => {
    onDataReceived(movies);
  }, [movies, onDataReceived]);

  return (
    <div>
      <h1>Action Movies</h1>
      <ul>
        {movies && movies.length > 0 ? (
          movies.map((movie, index) => (
            <li key={index}>
              <img src={movie.image} alt={movie.title} />
              <p>{movie.title}</p>
            </li>
          ))
        ) : (
          <p>No action movies available</p>
        )}
      </ul>
    </div>
  );
};

export default ActionMovies;
