import React from 'react'

const Validation = (details) => {
  return (
    <div>
        let errors={};
        

        if(!details.name){
            errors.name="Field is Required"

        }
        if(!details.Username){
            errors.Username="Field is Required"
        }

        if(!details.email){
            errors.email="Field is Required"
        }

        if(!details.number){
            errors.number="Field is Required"
        }
        return errors;


      
    </div>
  )
}

export default Validation;
